{
  "name": "Acme Software Inc.",
  "email": "billing@acmesoft.com",
  "phone": "+1-555-0123",
  "address": {
    "street": "123 Tech Street",
    "city": "San Francisco",
    "state": "CA",
    "zipCode": "94105",
    "country": "USA"
  },
  "taxId": "12-3456789",
  "paymentTerms": 30,
  "category": "software",
  "paymentMethods": [
    {
      "type": "bank_account",
      "details": {
        "accountNumber": "****1234",
        "routingNumber": "123456789"
      }
    }
  ],
  "autoPay": false,
  "approvalRequired": true,
  "approvalThreshold": 1000
}

{
  "name": "High Value Purchase Approval",
  "description": "Approval workflow for purchases over $1000",
  "amountThreshold": 1000,
  "vendorCategories": ["software", "equipment"],
  "steps": [
    {
      "name": "Manager Review",
      "type": "role",
      "role": "manager",
      "order": 1
    },
    {
      "name": "Finance Approval",
      "type": "role",
      "role": "finance",
      "order": 2
    }
  ]
}

{
  "name": "Invoice Automation Workflow",
  "description": "Automated invoice processing workflow",
  "category": "automation",
  "trigger": {
    "type": "webhook",
    "config": {
      "event": "invoice.created"
    }
  },
  "steps": [
    {
      "type": "log",
      "config": {
        "message": "Processing invoice"
      }
    },
    {
      "type": "email",
      "config": {
        "to": "finance@example.com",
        "subject": "New Invoice Created",
        "template": "invoice-notification"
      }
    }
  ]
}
